sprites.onOverlap(SpriteKind.Player, SpriteKind.Fireball, function (sprite, otherSprite) {
    if ((attack == 1 || jumpLock) && sprites.readDataNumber(otherSprite, "FireballPower") >= 3 && !(sprites.readDataBoolean(otherSprite, "ReturningFireball"))) {
        otherSprite.setVelocity((randint(-20, 20) + otherSprite.vx) * -1, (randint(-20, 20) + otherSprite.vy) * -1)
        otherSprite.x += -3
        otherSprite.y += -3
        sprites.setDataBoolean(otherSprite, "ReturningFireball", true)
    } else {
        scene.cameraShake(3, 100)
        music.play(music.melodyPlayable(music.smallCrash), music.PlaybackMode.InBackground)
        sprites.destroy(otherSprite)
        if (!(sprites.readDataBoolean(otherSprite, "ReturningFireball"))) {
            damageProcessingSystem(2 * sprites.readDataNumber(otherSprite, "FireballPower"))
            Explode(otherSprite.x, otherSprite.y, 1 * sprites.readDataNumber(otherSprite, "FireballPower"), sprites.readDataBoolean(otherSprite, "Super"))
        }
    }
})
