function beamShift (screen2: Image, width: number, height: number) {
    for (let indexX = 0; indexX <= width; indexX++) {
        for (let indexY = 0; indexY <= height; indexY++) {
            oldColor = screen2.getPixel(indexX - 45, indexY - 46)
            if (oldColor != 0) {
                if (oldColor == 1) {
                    newColor = 1
                }
                if (oldColor == 2) {
                    newColor = 3
                }
                if (oldColor == 3) {
                    newColor = 1
                }
                if (oldColor == 4) {
                    newColor = 5
                }
                if (oldColor == 5) {
                    newColor = 1
                }
                if (oldColor == 6) {
                    newColor = 5
                }
                if (oldColor == 7) {
                    newColor = 9
                }
                if (oldColor == 8) {
                    newColor = 7
                }
                if (oldColor == 9) {
                    newColor = 1
                }
                if (oldColor == 10) {
                    newColor = 11
                }
                if (oldColor == 11) {
                    newColor = 1
                }
                if (oldColor == 12) {
                    newColor = 10
                }
                if (oldColor == 13) {
                    newColor = 1
                }
                if (oldColor == 14) {
                    newColor = 13
                }
                if (oldColor == 15) {
                    newColor = 12
                }
                if (witherStormEyeTrace.getPixel(indexX, indexY) == 10) {
                    witherStormEyeTrace.setPixel(indexX, indexY, newColor)
                }
            }
        }
    }
}
