function placeCave (num: number) {
    lastCreatedCave = sprites.create(BottomCaveOverlay._pickRandom(), SpriteKind.Caves)
    lastCreatedCave.setFlag(SpriteFlag.Ghost, true)
    lastCreatedCave.setFlag(SpriteFlag.AutoDestroy, true)
    lastCreatedCave.bottom = scene.screenHeight() + 120
    lastCreatedCave.left = num
    lastCreatedCave.z = -10
}
function mobDamageProcessingSystem (damage: number, sprite: Sprite) {
    if (!(sprites.readDataBoolean(sprite, "internalIFrame"))) {
        sprites.setDataBoolean(sprite, "internalIFrame", true)
        sprites.changeDataNumberBy(sprite, "Health", Math.abs(damage) * -1)
        if (sprites.readDataNumber(sprite, "Health") <= 0) {
            animation.runImageAnimation(
            sprite,
            mobHurt[sprites.readDataNumber(sprite, "col")][sprites.readDataNumber(sprite, "row")],
            100,
            false
            )
            if (sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 1 || sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 7) {
                if (Math.percentChance(10)) {
                    dropArmorSystem(0)
                }
            }
            if (sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 2 || sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 8) {
                if (Math.percentChance(10)) {
                    dropArmorSystem(1)
                }
            }
            if (sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 3 || sprites.readDataNumber(sprite, "col") == 0 && sprites.readDataNumber(sprite, "row") == 9) {
                if (Math.percentChance(10)) {
                    dropArmorSystem(2)
                }
            }
            timer.after(50, function () {
                if (sprites.readDataBoolean(sprite, "LastMob")) {
                    for (let mob of sprites.allOfKind(SpriteKind.mob)) {
                        if (sprites.readDataNumber(mob, "Health") > 0) {
                            sprites.setDataBoolean(sprite, "LastMob", false)
                            mob.sayText("lastMob")
                            sprites.setDataBoolean(mob, "LastMob", true)
                        }
                    }
                }
                sprites.destroy(sprite, effects.ashes, 100)
                effects.clearParticles(sprite)
                info.changeScoreBy(10 * mobHealth[sprites.readDataNumber(sprite, "col")][sprites.readDataNumber(sprite, "row")])
                MobsKilledLevel += 1
            })
        } else {
            animation.runImageAnimation(
            sprite,
            mobHurt[sprites.readDataNumber(sprite, "col")][sprites.readDataNumber(sprite, "row")],
            100,
            false
            )
            timer.after(100, function () {
                animation.runImageAnimation(
                sprite,
                mobAnimation[sprites.readDataNumber(sprite, "col")][sprites.readDataNumber(sprite, "row")],
                sprites.readDataNumber(sprite, "AnimationSpeed"),
                true
                )
            })
        }
        timer.after(250, function () {
            sprites.setDataBoolean(sprite, "internalIFrame", false)
        })
    }
    return damage
}
sprites.onOverlap(SpriteKind.Player, SpriteKind.mobArrow, function (sprite, otherSprite) {
    sprites.destroy(otherSprite)
    damageProcessingSystem(sprites.readDataNumber(otherSprite, "MobArrowDamage"))
})
