function mobRunCreeper (Creeper: Sprite) {
    timer.after(2500, function () {
        if (sprites.readDataNumber(Creeper, "Health") > 0) {
            sprites.destroy(Creeper)
            Detonate(Creeper.x, Creeper.y + 12, 4)
        }
    })
}
