sprites.onOverlap(SpriteKind.playerArrow, SpriteKind.mob, function (sprite, otherSprite) {
    if (runGame) {
        if (!(fireworkBowUpgrade)) {
            mobDamageProcessingSystem(arrowCharge * 1, otherSprite)
        } else {
            mobDamageProcessingSystem(arrowCharge * 0.8, otherSprite)
            Explode(sprite.x - 2, sprite.y - 3, 4, false)
            fireworkExplosion = sprites.create(img`
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . 2 2 . . . . . . 
                . . . . . . . . 2 2 . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                . . . . . . . . . . . . . . . . 
                `, SpriteKind.noInteract)
            fireworkExplosion.setPosition(sprite.x - 25, sprite.y - 25)
            fireworkExplosion.startEffect(effects.ashes, 500)
            animation.runImageAnimation(
            fireworkExplosion,
            fireworkExplosions[0],
            100,
            false
            )
            fireworkExplosion.z = 200
            fireworkExplosion.setScale(randint(1.2, 1.5), ScaleAnchor.Middle)
            fireworkExplosion.lifespan = 400
            sprites.setDataBoolean(sprite, "isFirework", false)
        }
        otherSprite.sayText(sprites.readDataNumber(otherSprite, "Health"), 200, false)
        if (sprites.readDataNumber(otherSprite, "Health") <= 0) {
            otherSprite.vx = arrowCharge * 2.5
        } else {
            otherSprite.x += 15
            otherSprite.vx = arrowCharge * 2
            timer.after(150, function () {
                otherSprite.vx = sprites.readDataNumber(otherSprite, "MovementSpeed") * -1
            })
        }
        sprites.destroy(sprite)
    }
})
