function calcArrowPower (arrowPower: number) {
    maxArrowPower = 20
    minArrowPower = 0
    minX = 0.05
    maxX = 0.003
    clampedPower = Math.max(minArrowPower, Math.min(maxArrowPower, arrowPower))
    factor = (clampedPower - minArrowPower) / (maxArrowPower - minArrowPower)
    arrowFunctionOut = minX + factor * (maxX - minX)
    return arrowFunctionOut
}
