function RNG () {
    if (info.score() > 10000 && Math.percentChance(Math.min(30, 10 + Math.abs(info.score() / 2000)))) {
        RNGroll = 1
        return RNGroll
    } else if (info.score() > 2500 && Math.percentChance(Math.min(100, 20 + Math.abs(info.score() / 500)))) {
        RNGroll = 2
        return RNGroll
    } else if (info.score() > 500 && Math.percentChance(40)) {
        RNGroll = 3
        return RNGroll
    } else {
        if (info.score() > 20000) {
            RNGroll = 3
            return RNGroll
        } else {
            RNGroll = 4
            return RNGroll
        }
    }
}
