game.onUpdateInterval(5000, function () {
    if (runGame) {
        trySpawnFood(randint(1, 5))
        if (DoubleHealth) {
            if (saturation > 7 && health < 40) {
                saturation += -1
                health += 1
                updateSaturationHealthDisplay()
            }
        } else {
            if (saturation > 7 && health < 20) {
                saturation += -1
                health += 1
                updateSaturationHealthDisplay()
            }
        }
    }
})
forever(function () {
    if (runGame) {
        if (sprites.allOfKind(SpriteKind.mob).length > 0) {
            isMobRemaining = true
        } else {
            isMobRemaining = false
        }
    }
})
game.onUpdateInterval(20, function () {
    if (runGame) {
        if (witherStormActive) {
            if (showWitherBeam) {
                drawEyeBeam(eye1X, eye1Y, beam1Coords)
                if (Math.percentChance(5)) {
                    beamDebris(beam1Coords)
                }
                drawEyeBeam(eye2X, eye2Y, beam2Coords)
                if (Math.percentChance(5)) {
                    beamDebris(beam2Coords)
                }
                drawEyeBeam(eye3X, eye3Y, beam3Coords)
                timer.after(10, function () {
                    witherStormEyeTrace.fill(0)
                })
            }
        }
    }
})
game.onUpdateInterval(20, function () {
    if (OrbitalLaser) {
        OrbitalLaserRaytrace.drawLine(petAllay.x - 40, petAllay.y - 120, mob.x - 40, mob.y - 122, 1)
        OrbitalLaserRaytrace.drawLine(petAllay.x - 40, petAllay.y - 120, mob.x - 40, mob.y - 121, 9)
        OrbitalLaserRaytrace.drawLine(petAllay.x - 40, petAllay.y - 120, mob.x - 40, mob.y - 120, 9)
        OrbitalLaserRaytrace.drawLine(petAllay.x - 40, petAllay.y - 120, mob.x - 40, mob.y - 119, 9)
        OrbitalLaserRaytrace.drawLine(petAllay.x - 40, petAllay.y - 120, mob.x - 40, mob.y - 118, 1)
        LaserImpact.setPosition(mob.x, mob.y)
        LaserImpact.setScale(randint(0.8, 2.4), ScaleAnchor.Middle)
        timer.after(10, function () {
            OrbitalLaserRaytrace.fill(0)
        })
    }
})
